import React from 'react';
import '../styles/login.css';

class Login extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          name: '',
          pass:''
        };
    
        this.loadName = this.loadName.bind(this);
        this.loadPass = this.loadName.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    loadName(event) {
        this.setState({name: event.target.value});
    }

    loadPass(event) {
        this.setState({pass: event.target.value});
    }   

    handleSubmit(event) {
        alert('An essay was submitted: ' + this.state.name);
        event.preventDefault();
    }

    render(){
        return (
            <form className="conten" onSubmit={this.handleSubmit}>
               <span className="conten-top-barra"/>
               <div className="conten-login">
                     <img className="img-logo" src="https://www.viajarmilan.com/img/visitar-la-catedral-de-milan.jpg"/>
                     <input className="input-data-us" tipe="text" placeholder="Username" value={this.state.name} />
                     <input className="input-data-pa" tipe="text" placeholder="Password" value={this.state.pass} />
                     <div className="position-recovery">
                         <a href="#" className="recovery">Password recovery?</a>
                     </div>
                     <input type="submit" className="start-login" value="login" />
               </div>
            </form>
     
        );
    }

}

export default Login
