import ReactDOM from 'react-dom';
import './index.css' ;
import Card from './components/card'
import Welcome from './components/welcome';



const container=document.getElementById('root');
      container.classList.add("root");
/*esta funcion nos permite mostrar en pantalla un elemento
resive dos elementos 1-el emento 2-el contedor*/ 
ReactDOM.render(
<div>
 <Card
                     title="Card"
                     description="Hoy estamos junto a una leyenda"
                     colorLeft="blue"
                     colorRight="black"
 />
 <Welcome
     mensaje="Hola es un mensaje"
 />
 </div>
 ,container)

