import React from 'react';
import ReactDOM from 'react-dom';
import './index.css' ;


const user ={
    firstname:"john",
    lastname:"llanes"
};
function formatName(user){
  return user.firstname+' '+user.lastname;
}

const element=(
  <h1>Hola, {formatName(user)}!</h1>
);

const img= <img src="https://dwglogo.com/wp-content/uploads/2017/09/1460px-React_logo.png"></img>;

const container=document.getElementById('root');
/*esta funcion nos permite mostrar en pantalla un elemento
resive dos elementos 1-el emento 2-el contedor*/ 
ReactDOM.render(element,container)
