import React from 'react'
import Card from './card.js'
import Welcome from './welcome.js' 

class Page extends React.Component{
   constructor(props){
      super(props)
      this.state={
         data:[{
            "id":1,
            "title":"hola",
            "description":"hola mundo :D ",
            "mensaje":"Hoy es un gran dia",
            "colorLeft":"blue",
            "colorRight":"green",
         },
         {
            "id":2,
            "title":"lista",
            "description":"pasar lista",
            "mensaje":"Hoy es un lindo dia",
            "colorLeft":"black",
            "colorRight":"green",
         },
         {
            "id":3,
            "title":"Sing",
            "description":"cantar y bailar",
            "mensaje":"Hoy es un bonito dia para cantar y bailar",
            "colorLeft":"blue",
            "colorRight":"red",
         }]
      }
   }
   render(){
       return (
         <div>
            <Welcome
                 mensaje="Hola es un mensaje"
             />
             {
                this.state.data.map((cards)=>{
                  <Card
                  title="Card"
                  description="Hoy estamos juntos"
                  colorLeft="blue"
                  colorRight="black"
                  />
                })
             }

         
         </div>
       )
   }
}

export default Page
