import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './components/App.js';
import Login from './components/login.js'
import reportWebVitals from './components/reportWebVitals.js';

import { ApolloClient, InMemoryCache } from '@apollo/client';/*iniciar el cliente */

import { ApolloProvider } from '@apollo/client';/*para envolver el appp */

import { useQuery, gql } from '@apollo/client';/*la peticion */

const client = new ApolloClient({
    uri: 'http://3.134.101.34:8000/inventory/#query=query%7B%0A%20%20products%7B%0A%20%20%20%20name%0A%20%20%20%20brand%0A%20%20%7D%0A%7D',
    cache: new InMemoryCache()
});

const EXCHANGE_RATES = gql`
  query{
    products{
      name
      brand
    }
  }
`;

function ExchangeRates() {
  const { loading, error, data } = useQuery(EXCHANGE_RATES);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return data.rates.map(({ currency, rate }) => (
    console.log(currency,rate)
    /*
    <div key={currency}>
      <p>
        {currency}: {rate}
      </p>
    </div>
    */
  ));
}

document.getElementById('root').className+="root";
ReactDOM.render(
  /*
  <React.StrictMode>
    <App />
  </React.StrictMode>
  */
 <ApolloProvider client={client}>
     <ExchangeRates></ExchangeRates>
 </ApolloProvider>

  ,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
