import React from 'react'
import imgULR from "../asesst/img/noble6.png"
import '../styles/card.css' 
class Card extends React.Component{
  render(){
      const {title,description,colorLeft,colorRight}=this.props;
      return (
        <div className="card-container"
        style={{
          background:`linear-gradient(to left, ${colorLeft} 5%,${colorRight} 60%)`
        }}
        >
          <img className="card-img" src={imgULR}/>
          <div className="card-des">
            <h1 className="card-title">{title}</h1>
            <p>{description}</p>
          </div>
        </div>
        )
  }
}

export default Card
